import  React, {useState, useEffect} from "react";
//img must because import here
import logoSurfBetterHeader from '../img/common/logoSurfBetterHeader.png'
import slider1 from '../img/slider/slider1.jpg'
import slider2 from '../img/slider/slider2.jpg'
import slider3 from '../img/slider/slider3.jpg'


/**
 * Load login component
 * @returns {JSX.Element}
 * @constructor
 */
const LoginRegister = () => {

    /**
     * Use state for snackbar state (img being show)
     */
        // eslint-disable-next-line no-undef
    const [slideBarState, setSlideBarState] = useState(1)


    useEffect(() => {
        showSlide(0)
    },null)

    const showSlide = (n) => {
        debugger
        setSlideBarState(slideBarState +n )
        const x = document.getElementsByClassName('slide');
        console.log(slideBarState)
        // Note: if state of slide is bigger thant slides divs set one
        if (slideBarState > x.length) {
            setSlideBarState(1)
        }
        // Note: if the state of the slide is less than the number that 1 leaves
        // the state in the number of divs so that it jumps to the first
        if (slideBarState < 1) {
            setSlideBarState(x.length)
        }
        // Note: Load styles none and block
        for(let i = 0; i < x.length;i++){
            x[i].style.display = "none";
        }
        x[slideBarState-1].style.display = "block";
    }








    return (
        <div>
        <header>
            <img alt="SurfBetter logo" src={logoSurfBetterHeader}/>
            {/*modals buttons*/}
            <label for ="btn_modal_sign_in">
                <button title="Sign in the app">Sign ip</button>
            </label>
            <label for={"btn_modal_log_in"}>
                <button title="Log in the app">Log in</button>
            </label>
        </header>
        <main>
            <main className={"logSignMain"}>
                {/*SlideBar*/}
                <section className={"slideBar"}>
                    <div className={"slide"}>
                        <p>
                            A tool for the soul<br/>
                            for any surfer
                        </p>
                        <img srcSet={slider1} alt={"slider first item"}/>
                    </div>
                    <div className={"slide"}>
                        <p>
                            Find the best waves<br/>
                            of our bay
                        </p>
                        <img srcSet={slider2} alt={"slider second item"}/>
                    </div>
                    <div className={"slide"}>
                        <p>
                            Meet, compete and become<br/>
                            the best surfer in your area
                        </p>
                        <img srcSet={slider3} alt={"slider third item"}/>
                    </div>
                    {/*syle in line to cover the design point*/}
                    <div className={"sliderButtons"}>
                        <button style={{color: 'black'}} onClick={e => showSlide(-1)}>&#10094;</button>
                        <button style={{color: 'black'}} onClick={e => showSlide(1)}>&#10095;</button>
                    </div>


                </section>
            </main>
        </main>
        </div>


    )
}

export default LoginRegister
